  const [temp, setTemp]= useState("");
  const [size, setSize]=useState("");
  const [word, setWord]=useState("");



  useEffect(() => { 
    setqrCode 
 (`http://api.qrserver.com/v1/create-qr-code/?data=${word}!&size=${size}x${size}&bgcolor=${bgColor}`); 
